import React, { Component } from "react";
import styled from "styled-components";
import { motion } from "framer-motion";
import { media } from "../../Utils/media.js";

import WaterMeter from "../../Assets/Water Meter/meter.svg";

const ParentContainer = styled(motion.div)`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  width: 100%;
`;

const Container = styled(motion.div)`
  width: 80%;
  min-height: 500px;
  height: auto;
  display: flex;
  background-color: white;
  box-shadow: 3px 3px 20px rgba(170, 226, 156, 0.87),
    -3px -3px 20px rgba(217, 217, 217, 0.1);
  border-radius: 17px;
  padding: 10px;
  flex-wrap: wrap;
  justify-content: flex-start;
  align-items: center;
  margin: 10px;
  padding-top: 20px;
  flex-direction: column;

  ${media.phone`
      width : 90%;
      height: 90%;
    `};
`;

const HeaderItem = styled.div`
  display: flex;
  height: auto;
  width: 90%;
  margin: 2px;
  justify-content: flex-start;
  align-items: center;

  font-size: 32px;
  font-weight: 450;
  font-family: Montserrat;
`;

const SubHeaderItem = styled(HeaderItem)`
  font-size: 15px;
  font-weight: 400;
  font-family: Montserrat;
`;

const MiddleOrderItem = styled.div`
  display: flex;
  width: 90%;
  height: auto;
  flex-direction: row;
  justify-content: space-around;
`;

const CarouselContainer = styled.div`
  display: flex;
  width: auto;
  height: auto;
  padding: 20px;
  flex-direction: column;
`;

const CarouselCards = styled(motion.div)`
  display: flex;
  width: 300px;
  height: 250px;
  padding: 2px;
  flex-direction: column;
  border-radius: 10px;
  box-shadow: 15px 15px 60px rgba(100, 145, 198, 0.25),
    -15px -15px 60px rgba(0, 0, 0, 0.25);

  margin: 5px;
`;

const CarouselButton = styled(motion.div)`
  width: 0;
  height: 0;
  border-top: 10px solid transparent;
  border-bottom: 10px solid transparent;
  border-left: 10px solid #0041be;
  border-radius: 3px;
`;

const CarouselLegend = styled(motion.div)`
  width: 10px;
  height: 10px;
  display: flex;
  border-radius: 20px;
  border: none;
`;

const CarouselTopContainer = styled(motion.div)`
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 2px;
`;

const CarouselBottomContainer = styled(motion.div)``;

class Carousel extends Component {
  constructor() {
    super();
    this.state = {
      currentPageCount: 0,
    };
  }

  handlePageIncrement = () => {
    let count = this.state.currentPageCount;
    this.setState({
      currentPageCount: count++,
    });
  };

  handlePageDecrement = () => {
    let count = this.state.currentPageCount;
    this.setState({
      currentPageCount: count--,
    });
  };
  render() {
    return (
      <CarouselContainer>
        <CarouselTopContainer>
          <CarouselButton
            initial={{ rotate: 180 }}
            whileTap={{ scale: 0.88 }}
          ></CarouselButton>
          {this.props.content.map((value, index) => (
            <CarouselCards
              initial={{ opacity: 0 }}
              animtate={{ opacity: [0, 1] }}
              transition={{ duration: 0.1, ease: "linear" }}
            >
              {value.header}
              {value.content}
            </CarouselCards>
          ))}
          ><CarouselButton whileTap={{ scale: 0.88 }}></CarouselButton>
        </CarouselTopContainer>
        <CarouselBottomContainer></CarouselBottomContainer>
      </CarouselContainer>
    );
  }
}

const CarouselCardsContent = [
  {
    header: "Save more than 35% water!",
    content:
      "Kamstrup ultrasonic smart water meter enables induvidual users to measure and monitor their water consumption in real time.",
  },
  {
    header: "Pay ONLY for what you use!",
    content:
      "Many residential apartments do not monitor the induvidual consumption of water and are being charged a a fixed rate. This makes people unaware of their water usage resulting in water wastage.",
  },
];

export default class WaterMeterPage extends Component {
  constructor() {
    super();
  }

  render() {
    return (
      <ParentContainer>
        <Container>
          <HeaderItem> MULTICAL® 21 / flowIQ® 210x </HeaderItem>
          <SubHeaderItem>
            Ultrasonic water meter optimised for residential use{" "}
          </SubHeaderItem>
          <MiddleOrderItem>
            <Carousel content={CarouselCardsContent}></Carousel>
            <img src={WaterMeter} style={{ transform: "scale(0.8)" }} />
          </MiddleOrderItem>
        </Container>
        <Container></Container>
      </ParentContainer>
    );
  }
}
