import React, { Component } from "react";
import styled from "styled-components";
import { motion } from "framer-motion";
import { media } from "../../Utils/media.js";

import WaterMeter from "../../Assets/Water Meter/meter.svg";

const ParentContainer = styled(motion.div)`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  width: 100%;
`;

const Container = styled(motion.div)`
  width: 80%;
  min-height: 500px;
  height: auto;
  display: flex;
  background-color: white;
  box-shadow: 3px 3px 20px rgba(170, 226, 156, 0.87),
    -3px -3px 20px rgba(217, 217, 217, 0.1);
  border-radius: 17px;
  padding: 10px;
  flex-wrap: wrap;
  justify-content: flex-start;
  align-items: center;
  margin: 10px;
  padding-top: 20px;
  flex-direction: column;

  ${media.phone`
      width : 90%;
      height: 90%;
    `};
`;

const HeaderItem = styled.div`
  display: flex;
  height: auto;
  width: 90%;
  margin: 2px;
  justify-content: flex-start;
  align-items: center;

  font-size: 32px;
  font-weight: 450;
  font-family: Montserrat;
`;

const SubHeaderItem = styled(HeaderItem)`
  font-size: 15px;
  font-weight: 400;
  font-family: Montserrat;
`;

const MiddleOrderItem = styled.div`
  display: flex;
  width: 90%;
  height: auto;
  flex-direction: row;
`;

export default class WaterMeterPage extends Component {
  constructor() {
    super();
  }

  render() {
    return (
      <ParentContainer>
        <Container>
          <HeaderItem> MULTICAL® 21 / flowIQ® 210x </HeaderItem>
          <SubHeaderItem>
            Ultrasonic water meter optimised for residential use{" "}
          </SubHeaderItem>
          <MiddleOrderItem>
            <img src={WaterMeter} />
          </MiddleOrderItem>
        </Container>
        <Container></Container>
      </ParentContainer>
    );
  }
}
