import React, { Component } from "react";
import styled from "styled-components";
import { media } from "../../Utils/media.js";
import { motion } from "framer-motion";

const Wrapper = styled(motion.div)`
  display: none;
  position: fixed;
  z-index: 1;
  left: 0;
  top: 0;
  width: 100%;
  height: 100%;
  overflow: auto;ReadMore
  background-color: rrgba(255, 251, 251, 0.03);
  backdrop-filter: blur(35px);
  justify-content: center;
  align-items: center;
  overflow-y: hidden;
`;

const SplashScreen = styled(motion.div)`
  display: flex;
  background-color: #fefefe;
  margin: auto;
  padding: 10px;
  border: none;
  border-radius: 10px;
  width: 90%;
  height: 90%;
  box-shadow: 20px 20px 60px rgba(170, 231, 209, 0.25),
    -20px -20px 60px rgba(163, 230, 189, 0.25);
  flex-direction: column;
`;

const SplashScreenHeader = styled.div`
  display: flex;
  width: -webkit-fill-available;
  height: auto;
  padding: 8px;
  font-size: 14px;
  font-weight: 450;
  color: blue;
  justify-content: flex-start;
  align-items: center;
`;

export default class ModalProductShowcase extends Component {
  constructor() {
    super();
  }

  render() {
    return (
      <Wrapper
        style={{ display: this.props.showModal ? "flex" : "none" }}
        animate={{ opacity: [0, 1] }}
        transition={{ duration: 0.2, ease: "linear" }}
      >
        <SplashScreen
          initial={{ opacity: 0 }}
          animate={{ opacity: [0, 1] }}
          transition={{ delay: 0.3, ease: "linear" }}
        >
          <SplashScreenHeader>HEY</SplashScreenHeader>
        </SplashScreen>
      </Wrapper>
    );
  }
}
