import React, { Component } from "react";
import styled from "styled-components";
import { motion } from "framer-motion";
import { media } from "../../Utils/media.js";
import YouTube from "react-youtube";

import WaterMeter from "../../Assets/Water Meter/meter.svg";
import RainbowResidency from "../../Assets/Water Meter/rainbow-residency.png";

const ParentContainer = styled(motion.div)`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  width: 100%;
`;

const Container = styled(motion.div)`
  width: 80%;
  min-height: 500px;
  height: auto;
  display: flex;
  background-color: white;
  box-shadow: 3px 3px 20px rgba(170, 226, 156, 0.87),
    -3px -3px 20px rgba(217, 217, 217, 0.1);
  border-radius: 17px;
  padding: 10px;
  flex-wrap: wrap;
  justify-content: flex-start;
  align-items: center;
  margin: 10px;
  padding-top: 20px;
  flex-direction: column;

  ${media.phone`
      width : 90%;
      height: 90%;
    `};
`;

const HeaderItem = styled.div`
  display: flex;
  height: auto;
  width: 90%;
  margin: 2px;
  justify-content: flex-start;
  align-items: center;

  font-size: 32px;
  font-weight: 450;
  font-family: Montserrat;
`;

const SubHeaderItem = styled(HeaderItem)`
  font-size: 15px;
  font-weight: 400;
  font-family: Montserrat;
`;

const MiddleOrderItem = styled.div`
  display: flex;
  width: 90%;
  height: auto;
  flex-direction: row;
  justify-content: space-around;

  ${media.phone`

  flex-direction: column;
  padding: 0;
  padding-top: 30px;
  `}
`;

const BottomOrderItem = styled.div`
  display: flex;
  width: 90%;
  height: auto;
  flex-direction: column;
  justify-content: center;
  align-items: flex-end;
  padding: 10px;
  font-size: 12px;
`;

const CarouselContainer = styled.div`
  display: flex;
  width: auto;
  height: auto;
  padding: 20px;
  flex-direction: column;
  justify-content: center;
`;

const CarouselCards = styled(motion.div)`
  display: flex;
  width: 400px;
  min-height: 300px;

  padding: 8px;
  flex-direction: column;
  border-radius: 10px;
  box-shadow: 15px 15px 60px rgba(100, 145, 198, 0.25),
    -15px -15px 60px rgba(0, 0, 0, 0.25);

  justify-contents: flex-start;
  color: black;
  margin: 5px;
`;

const CarouselCardHeader = styled.div`
  display: flex;
  width: -webkit-fill-available;
  height: auto;
  padding: 5px;
  font-size: 32px;
  font-weight: 400;
  color: #13567c;
`;

const CarouselCardBody = styled(CarouselCardHeader)`
  font-size: 15px;
  font-weight: 400;
  color: #13567c;
  color: black;
  display: flex;
  justify-self: center;
  flex-direction: column;
`;

const CarouselButton = styled(motion.div)`
  width: 0;
  height: 0;
  border-top: 10px solid transparent;
  border-bottom: 10px solid transparent;
  border-left: 10px solid #0041be;
  border-radius: 3px;
`;

const CarouselLegend = styled(motion.div)`
  width: 10px;
  height: 10px;
  display: flex;
  border-radius: 20px;
  border: none;
  margin-left: 5px;
  margin-right: 5px;
  box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
`;

const CarouselTopContainer = styled(motion.div)`
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 2px;
`;

const CarouselBottomContainer = styled(motion.div)`\
  display: flex;
  width: auto;
  height: auto;
  padding: 5px;
  justify-content: center;
  align-items: center;
  margin: 5px;
`;

const ContentContainer = styled.div`
  display: flex;
  width: 95%;
  height: auto;
  padding: 5px;
  justify-content: center;
  align-items: center;
  margin: 5px;

  flex-direction: column;
`;

const ContentRowBars = styled(motion.div)`
  display: flex;
  width: 90%;
  height: 50px;
  border-radius: 10px;
  box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
  margin-top: 12px;
`;

const ContentRowBarsHeader = styled(motion.div)`
  display: flex;
  width: 100%;
  height: auto;
  justify-content: flex-start;
  align-items: center;
  margin: 2px;
  padding-left: 10px;
  align-self: flex-start;
  font-size: 24px;
  color: #13567c;
  font-weight: 400;
  padding-top: 10px;
`;

const DropDownContent = [
  {
    header: "Benefits",
    content: "",
  },
  {
    header: "Battery Life",
    content: "",
  },
  {
    header: "Communication & Remote Reading",
    content: "",
  },
  {
    header: "Construction & Packagin",
    content: "",
  },
];

class DropDownContentDisplay extends Component {
  constructor() {
    super();
    this.state = {
      dropDownStates: DropDownContent,
    };
  }

  componentDidMount() {
    let restructuredDropDownContents = DropDownContent.map((value, index) => ({
      header: value.header,
      content: value.content,
