import React, { Component } from "react";
import styled from "styled-components";
import { motion } from "framer-motion";

import { media, mediaType } from "../../Utils/media.js";

const ParentContainer = styled(motion.div)`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  width: 100%;
`;

const Container = styled(motion.div)`
  width: 80%;
  min-height: 500px;
  height: auto;
  display: flex;
  background-color: white;
  box-shadow: 20px 20px 60px rgba(170, 226, 156, 0.87),
    -20px -20px 60px rgba(217, 217, 217, 0.19);
  border-radius: 17px;
  padding: 10px;
  flex-direction: column;

  ${media.phone`
      width : 90%;
      height: 90%;
    `};
`;

const ContainerHeader = styled(h1)`
  display: flex;
  width: 100%;
  padding: 5px;
  background-color: red;
  height: auto;
  justify-content: flex-start;
  align-items: center;

  color: #1e7c43;
  font-size: 24px;
`;

export default class AboutUs extends Component {
  constructor() {
    super();
  }

  render() {
    return (
      <ParentContainer>
        <Container>
          <ContainerHeader>About US</ContainerHeader>
        </Container>
      </ParentContainer>
    );
  }
}
