import React, { Component } from "react";
import styled from "styled-components";

import { media, mediaTypes } from "../../Utils/media.js";
import { motion } from "framer-motion";

const ParentContainer = styled(motion.div)`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  width: 100%;
`;

const Container = styled(motion.div)`
  width: 80%;
  min-height: 500px;
  height: auto;
  display: flex;
  background-color: white;
  box-shadow: 20px 20px 60px rgba(170, 226, 156, 0.87),
    -20px -20px 60px rgba(217, 217, 217, 0.19);
  border-radius: 17px;
  padding: 10px;
  flex-direction: column;

  ${media.phone`
      width : 90%;
      height: 90%;
    `};
`;

const HeaderItem = styled.div`
  display: flex;
  height: auto;
  width: 90%;
  margin: 2px;
  justify-content: flex-start;
  align-items: center;
  color: #1e7c43;
  font-size: 24px;
  font-weight: 500;

  font-family: Montserrat;
`;

const ServiceCardsContainer = styled(motion.div)`
  width: 90%;
  height: 50px;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: #def4d9;
  justify-self: center;
  border-radius: 10px;
  align-self: center;
  box-shadow: 5px 5px 6px #e6d6d6;
  margin-top: 20px;
  padding: 10px;
`;

const ServiceList = [
  {
    content: "High End Data Acquisition Systems",
  },
  {
    content:
      "Retrofitting of conventional type of PLC automation panel building",
  },
  {
    content: "Application engineering assitance ins selection equipment",
  },
  {
    content: "Turnkey contract from concept to commissioning.",
  },
  {
    content: "Installation and commissioning assistance.",
  },
  {
    content: "Troubleshooting.",
  },
  {
    content: "Maintenance contracts and supply of spare parts.",
  },
  {
    content: "BTU meter Training.",
  },
];

const container = {
  hidden: { opacity: 0 },
  visible: {
    opacity: 1,

    transition: {
      when: "beforeChildren",
      staggerChildren: 0.1,
    },
  },
};

const item = {
  hidden: { y: 15, opacity: 0 },
  visible: {
    y: 0,
    opacity: 1,
  },
};

export default class ServicesPage extends Component {
  constructor() {
    super();
  }

  render() {
    return (
      <ParentContainer>
        <Container variants={container} initial="hidden" animate="visible">
          <HeaderItem>Services</HeaderItem>
          {ServiceList.map((value, index) => (
            <ServiceCardsContainer
              style={{ alignSelf: index % 2 ? "flex-start" : "flex-end" }}
              variants={item}
              whileTap={{ scale: 0.88 }}
              whileHover={{ boxShadow: "5px 5px 15px #e6d6d6" }}
              transition={{ duration: 0.2, ease: "linear" }}
            >
              {value.content}
            </ServiceCardsContainer>
          ))}
        </Container>
      </ParentContainer>
    );
  }
}
