import React , { Component} from "react";
import styled from "styled-components";
import {motion} from "framer-motion";
import {media, mediaType} from "../../Utils/media.js";


const ParentContainer = styled(motion.div)`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  width: 100%;
`;

const Container = styled(motion.div)`
  width: 95%;
  min-height: 500px;
  height: auto;
  display: flex;
  background-color: white;
  box-shadow: 20px 20px 60px rgba(170, 226, 156, 0.87),
    -20px -20px 60px rgba(217, 217, 217, 0.19);
  border-radius: 17px;
  padding: 10px;
  flex-direction: column;
  justify-content: flex-start;
  align-items: center;
  ${media.phone`
      width : 90%;
      height: 90%;
    `};
`;

const FormContainer = styled.div`
  width : auto%;
  height: auto;
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-top: 50px;
  flex-direction: column;
`;

const FormLabel  = styled.div`
  margin-right :  auto;
  margin-top: 20px;
`;

const FormInput = styled.div`
  margin-top : 5px;
`;


const FormElement = styled.input`
  width : 100%;
  border-radius: 3px;
  border : 1px solid grey;
  height: 35px;
  width : 250px;
`;


const TextAreaCustom = styled.textarea`
  width : 500px;
  height:
`;

export default class ContactUS extends Component {
  constructor() {
      super();
  }

  render(){
      return (
          <ParentContainer>
              <Container>
                <FormContainer>
                  <FormLabel>
                      Name
                  </FormLabel>
                  <FormInput>
                      <FormElement></FormElement>
                  </FormInput>
                  <FormLabel>
                      Organisation
                  </FormLabel>
                  <FormInput>
                      <FormElement></FormElement>
                  </FormInput>
                  <FormLabel>
                      Designation
                  </FormLabel>
                  <FormInput>
                      <FormElement></FormElement>
                  </FormInput>
                  <FormLabel>
                      Organisation
                  </FormLabel>
                  <FormInput>
                      <FormElement></FormElement>
                  </FormInput>
                  <FormLabel>
                      Country
                  </FormLabel>
                  <FormInput>
                      <FormElement></FormElement>
                  </FormInput>
                  <FormLabel>
                      Email
                  </FormLabel>
                  <FormInput>
                      <FormElement></FormElement>
                  </FormInput>

                </FormContainer>
                <FormContainer>
                <FormLabel>
                      Message
                  </FormLabel>
                  <FormInput>
                      <TextAreaCustom name="description" rows="10" col="55">

                      </TextAreaCustom>
                  </FormInput>
                  </FormContainer>
              </Container>
          </ParentContainer>
      )
  }
}
